const Log = new Logger('DomReader');
/**
 * @type {Object} Default settings
 */
const Defaults = {
  selector: '[data-component]',
  element: document,
};

/**
 * Read the DOM and check for Zero Components
 * @class
 */
class DomReader {
  /**
     * Set the element for all operations
     * If falsy value is passed it returns the default element `document`
     * @see https://developer.mozilla.org/en-US/docs/Glossary/Falsy
     * @param {Element} element
     */
  set Element(element) {
    this.element = element;
  }

  /**
     * Get the element. If not defined return the document element
     * @returns {Element|document}
     * @see https://developer.mozilla.org/en-US/docs/Web/API/Element
     */
  get Element() {
    return !this.element ? Defaults.element : this.element;
  }

  /**
     * Set the element selector using CSS selector specs
     * @see https://developer.mozilla.org/en-US/docs/Learn/CSS/Introduction_to_CSS/Selectors
     * If falsy value is passed it returns the default selector [data-component]
     * @see https://developer.mozilla.org/en-US/docs/Glossary/Falsy
     * @param {String} selector
     */
  set Selector(selector) {
    this.selector = selector;
  }

  get Selector() {
    return !this.selector ? Defaults.selector : this.selector;
  }

  /**
     * Return the list of components matching given selector
     *@returns {NodeList}
     */
  check() {
    const components = this.Element.querySelectorAll(this.Selector);
    return components;
  }
}
export default DomReader;

import Logger from '@openmind/litelog';

/**
 * The Logger
 */
const Log = new Logger('Broadcast');
/**
 * @type {Object} Deafult settings
 */
const Defaults = {
  ID: 'zero-broadcaster',
  element: 'span',
};
/**
 * Grab and dispatch messages throug the dom
 * @class
 */
class Broadcast {
  /**
   * Get the Broadcaster element
   * @returns {Element} the element used as Broadcaster
   */
  get Broadcaster() {
    return this.broadcaster;
  }

  /**
   * Cast a message throug the dom
   * @param {String} msg message to cast
   * @param {Object} obj callback to execute on message receiving
   * @see https://developer.mozilla.org/en-US/docs/Web/API/CustomEvent/CustomEvent#Polyfill
   */
  cast(msg, obj = {}) {
    Log.log(`cast:${msg}`);
    const detail = Object.assign({}, {
      detail: {},
    }, {
      detail: obj,
    });
    const event = new CustomEvent(`msg:${msg}`, detail);
    this.Broadcaster.dispatchEvent(event, obj);
  }

  /**
   * Add the listener for given event and dispatch the event
   * @param {String} msg message to cast
   * @param {Function} callback callback to execute on message receiving
   */
  grab(msg, callback) {
    Log.log(`grab:${msg}`);
    this.Broadcaster.addEventListener(`msg:${msg}`, callback, { capture: true, passive: true });
  }

  /**
   * Remove the listener for given message
   * @param {String} msg message to cast
   * @param {Function} callback callback to execute on message receiving
   */
  ungrab(msg, callback) {
    this.Broadcaster.removeEventListener(`msg:${msg}`, callback);
  }

  /**
   * Initialize the Broadcast
   */
  constructor() {
    const broadcaster = document.createElement(Defaults.element);
    broadcaster.setAttribute('id', Defaults.ID);
    /**
     * @type {Element}
     */
    this.broadcaster = broadcaster;
  }
}

export default Broadcast;

